+            text-align: center;
+            text-transform: uppercase;
+        }
+        .error-msg {
+            color:$common-error;
+            margin-top:10px;
+            text-align: center
+        }
+        .notify-msg {
+            color:$common-notify;
+            margin-top:10px;
+            text-align: center
+        }
